import React, { useState } from 'react';
import './Slider.css';
import image1 from './img1.jpg'; // Import your images
import image2 from './img2.jpg';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa'; // Import arrow icons from React Icons

const Slider = () => {
  const images = [image1, image2]; // List of imported images
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const goToNextSlide = () => {
    const newIndex = (currentImageIndex + 1) % images.length;
    setCurrentImageIndex(newIndex);
  };

  const goToPrevSlide = () => {
    const newIndex = (currentImageIndex - 1 + images.length) % images.length;
    setCurrentImageIndex(newIndex);
  };

  return (
    <div className="slider-container">
      <div className="slider-dropdown">
        {/* Dropdown menu code here */}
        <ul className="dropdown-menu">
          <li>Option 1</li>
          <li>Option 2</li>
          <li>Option 3</li>
        </ul>
      </div>
      <div className="slider-frame">
        <img src={images[currentImageIndex]} alt={`Slide ${currentImageIndex + 1}`} className="slider-image" />
      </div>
      <div className="slider-controls">
        <button onClick={goToPrevSlide} className="slider-button prev">
          <FaArrowLeft />
        </button>
        <button onClick={goToNextSlide} className="slider-button next">
          <FaArrowRight />
        </button>
      </div>
    </div>
  );
};

export default Slider;
