import React, { useState, useEffect } from 'react';
import './Slider.css';
import { FaAngleLeft, FaAngleRight } from 'react-icons/fa'; // Import arrow icons from React Icons

const Slider = ({ images }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(goToNextSlide, 3000); // Automatic slide change every 3 seconds
    return () => clearInterval(intervalId); // Cleanup the interval on component unmount
  }, [currentImageIndex]);

  const goToNextSlide = () => {
    const newIndex = (currentImageIndex + 1) % images.length;
    setCurrentImageIndex(newIndex);
  };

  const goToPrevSlide = () => {
    const newIndex = (currentImageIndex - 1 + images.length) % images.length;
    setCurrentImageIndex(newIndex);
  };

  return (
    <div className="slider-container">
      <div className="slider-frame">
        <img src={images[currentImageIndex]} alt={`Slide ${currentImageIndex + 1}`} className="slider-image" />
      </div>
      <div className="slider-controls">
        <button onClick={goToPrevSlide} className="slider-button prev">
          <FaAngleLeft />
        </button>
        <button onClick={goToNextSlide} className="slider-button next">
          <FaAngleRight />
        </button>
      </div>
    </div>
  );
};

export default Slider;
