import React, { useState, useEffect } from 'react';
import './Slider.css';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa'; // Import arrow icons from React Icons
import image1 from './img1.jpg'; // Import your images
import image2 from './img2.jpg';

const Slider = () => {
  const images = [image1, image2]; // List of imported images
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isHovered, setIsHovered] = useState(false);

  useEffect(() => {
    const intervalId = setInterval(goToNextSlide, 5000); // Automatic slide change every 5 seconds
    return () => clearInterval(intervalId); // Cleanup the interval on component unmount
  }, [currentImageIndex]);

  const goToNextSlide = () => {
    const newIndex = (currentImageIndex + 1) % images.length;
    setCurrentImageIndex(newIndex);
  };

  const goToPrevSlide = () => {
    const newIndex = (currentImageIndex - 1 + images.length) % images.length;
    setCurrentImageIndex(newIndex);
  };

  return (
    <div className="slider-container" onMouseEnter={() => setIsHovered(true)} onMouseLeave={() => setIsHovered(false)}>
      <div className={`slider-frame ${isHovered ? 'hovered' : ''}`}>
        <img src={images[currentImageIndex]} alt={`Slide ${currentImageIndex + 1}`} className="slider-image" />
      </div>
      <div className="slider-controls">
        <button onClick={goToPrevSlide} className="slider-button prev">
          <FaArrowLeft />
        </button>
        <button onClick={goToNextSlide} className="slider-button next">
          <FaArrowRight />
        </button>
      </div>
    </div>
  );
};

export default Slider;
