import React, { useState, useEffect } from 'react';
import './Slider.css'; // Import your CSS file for styling

// Assuming you have imported your images as variables or from a directory
const image1 = require('./img1.jpg');
const image2 = require('./img2.jpg');

const Slider = () => {
  const [currentImage, setCurrentImage] = useState(1);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage(currentImage === 1 ? 2 : 1); // Toggle between image 1 and 2
    }, 5000); // Change image every 5 seconds

    return () => clearInterval(interval); // Clean up interval on component unmount
  }, [currentImage]);

  const handlePrev = () => {
    setCurrentImage(currentImage === 1 ? 2 : 1);
  };

  const handleNext = () => {
    setCurrentImage(currentImage === 2 ? 1 : 2);
  };

  return (
    <div className="slider-container">
      <img
        src={currentImage === 1 ? image1.default : image2.default}
        alt={`Image ${currentImage}`}
        className="slider-image"
      />
      <div className="navigation-buttons">
        <button className="arrow-button prev" onClick={handlePrev}>
          <img src={require('./path_to_left_arrow_icon.svg').default} alt="Previous" />
        </button>
        <button className="arrow-button next" onClick={handleNext}>
          <img src={require('./path_to_right_arrow_icon.svg').default} alt="Next" />
        </button>
      </div>
    </div>
  );
};

export default Slider;
