import React, { useState, useEffect } from 'react';
import './Slider.css';

// Import images
import image1 from './images/img1.jpg';
import image2 from './images/img2.jpg';

const Slider = () => {
  // Define array of images
  const images = [image1, image2, image3];
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(goToNextSlide, 3000); // Automatic slide change every 3 seconds
    return () => clearInterval(intervalId); // Cleanup on unmount
  }, [currentImageIndex]);

  const goToNextSlide = () => {
    const nextIndex = (currentImageIndex + 1) % images.length;
    setCurrentImageIndex(nextIndex);
  };

  const goToPrevSlide = () => {
    const prevIndex = (currentImageIndex - 1 + images.length) % images.length;
    setCurrentImageIndex(prevIndex);
  };

  return (
    <div className="slider-container">
      <div className="slider-frame" style={{ transform: `translateX(-${currentImageIndex * 100}%)` }}>
        {images.map((image, index) => (
          <img key={index} src={image} alt={`Slide ${index + 1}`} className="slider-image" />
        ))}
      </div>
      <button className="slider-button prev" onClick={goToPrevSlide}>
        &#10094; {/* Left arrow */}
      </button>
      <button className="slider-button next" onClick={goToNextSlide}>
        &#10095; {/* Right arrow */}
      </button>
    </div>
  );
};

export default Slider;
