import React, { useState, useEffect } from 'react';
import './Slider.css';
import image1 from './img1.jpg'; // Import your images
import image2 from './img2.jpg';

const Slider = () => {
  const images = [image1, image2]; // List of imported images
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(goToNextSlide, 3000); // Automatic slide change every 3 seconds
    return () => clearInterval(intervalId); // Cleanup the interval on component unmount
  }, [currentImageIndex]);

  const goToNextSlide = () => {
    const newIndex = (currentImageIndex + 1) % images.length;
    setCurrentImageIndex(newIndex);
  };

  const goToPrevSlide = () => {
    const newIndex = (currentImageIndex - 1 + images.length) % images.length;
    setCurrentImageIndex(newIndex);
  };

  const handleDotClick = (index) => {
    setCurrentImageIndex(index);
  };

  return (
    <div className="slider-container">
      <div className="slider-frame">
        {images.map((image, index) => (
          <img key={index} src={image} alt={`Slide ${index + 1}`} className="slider-image" />
        ))}
      </div>
      <div className="slider-dots">
        {images.map((_, index) => (
          <button
            key={index}
            onClick={() => handleDotClick(index)}
            className={`slider-dot ${index === currentImageIndex ? 'active' : ''}`}
          ></button>
        ))}
      </div>
    </div>
  );
};

export default Slider;
