import React, { useState, useEffect } from 'react';
import './Slider.css';
import image1 from './images/img1.jpg'; // Import your images
import image2 from './images/img2.jpg';

const Slider = () => {
  const images = [image1, image2]; // List of imported images
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isAuto, setIsAuto] = useState(true);

  const goToNextSlide = () => {
    const newIndex = (currentImageIndex + 1) % images.length;
    setCurrentImageIndex(newIndex);
  };

  const goToPrevSlide = () => {
    const newIndex = (currentImageIndex - 1 + images.length) % images.length;
    setCurrentImageIndex(newIndex);
  };

  const toggleAuto = () => {
    setIsAuto(!isAuto);
  };

  useEffect(() => {
    let intervalId = null;

    if (isAuto) {
      intervalId = setInterval(() => {
        goToNextSlide();
      }, 1000);
    }

    return () => {
      if (intervalId) {
        clearInterval(intervalId);
      }
    };
  }, [currentImageIndex, isAuto]);

  return (
    <div className="slider-container">
      <img src={images[currentImageIndex]} alt={`Slide ${currentImageIndex + 1}`} className="slider-image" />
      <div className="slider-controls">
        <button onClick={goToPrevSlide}>Prev</button>
        <button onClick={toggleAuto}>{isAuto ? 'Pause' : 'Play'}</button>
        <button onClick={goToNextSlide}>Next</button>
      </div>
    </div>
  );
};

export default Slider;
