import React, { useState } from 'react';
import axios from 'axios';

const LoginForm = () => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async () => {
    try {
      // Make HTTP POST request to your backend authentication endpoint
      const response = await axios.post('http://localhost:5000/login', { userId, password });
      const { token } = response.data;
      
      // Handle successful login (e.g., store token in localStorage)
      localStorage.setItem('token', token);

      switch(userId){
        case '4444':
          window.location.href = '/ACP';
          break;
        case '5555':
          window.location.href = '/PI';
          break;
        case '6666':
          window.location.href = '/SI';
          break;
        default:
          break;
      }
      // Redirect or perform other actions after successful login
    } catch (error) {
      // Handle authentication error
      setErrorMessage('Invalid credentials');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {errorMessage && <p>{errorMessage}</p>}
      <input type="text" placeholder="User ID" value={userId} onChange={(e) => setUserId(e.target.value)} />
      <br/>
      <input type="text" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <br/>
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default LoginForm;
