import React, { useState } from 'react';
import { Link } from 'react-router-dom'; 
import { useTranslation } from 'react-i18next';
import { RiArrowDownSLine } from 'react-icons/ri'; // Import the arrow down icon from React Icons
import './Navbar.css';

const Navbar = () => {
  const { t } = useTranslation(); // Hook for translations
  const [showDropdown, setShowDropdown] = useState(false);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo"><Link className='logolink' to="/">KSP Datathon 2024</Link></div>
      <ul className="navbar-links">
        <li  className="navbar-link"><Link className='link' to="/">Home</Link></li>
        <li className="navbar-link">
            <Link className="link" to="/about">About</Link>
        </li>
        <li className="navbar-link"><Link className='link'  to="/services">Services</Link></li>
        <li className="navbar-link"><Link className='link' to="/contact">Contact</Link></li>
        <li className="navbar-link" onClick={toggleDropdown}>
          Profile <RiArrowDownSLine />
          {/* Dropdown menu */}
          {showDropdown && (
            <ul className="dropdown-menu">
              <li><Link className='droplink' to="/login">Log in</Link></li>
              <li><Link className='droplink' to="/logout">Log out</Link></li>
              {/* Add more options as needed */}
            </ul>
          )}
        </li>
      </ul>
    </nav>
  );
};
export default Navbar;
