import React, { useState } from 'react';
import { RiArrowDownSLine } from 'react-icons/ri'; // Import the arrow down icon from React Icons
import './SecondaryNavbar.css';

const SecondaryNavbar = () => {
  const [showDropdown, setShowDropdown] = useState(false);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  return (
    <nav className="secondary-navbar">
      <ul className="secondary-navbar-links">
        <li className="secondary-navbar-link">About <RiArrowDownSLine /></li>
        {/* Dropdown menu for About */}
        {showDropdown && (
          <ul className="dropdown-menu">
            <li>About Us</li>
            <li>Administration</li>
            <li>Training</li>
            <li>Police Units and Special Units</li>
            {/* Add more options as needed */}
          </ul>
        )}
      </ul>
    </nav>
  );
};

export default SecondaryNavbar;
